mapper.select:
SELECT 

FROM bus_oe_experience_order a

WHERE a.id = #{id}

SELECT 

FROM bus_oe_experience_order a

     LEFT JOIN bus_pj_produce_judge pj ON pj.experience_order_id = a.id
     LEFT JOIN bus_pj_produce_judge_photo pjp ON pjp.produce_judge_id = pj.id

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.order_no = #{orderNo}


		AND a.member_id = #{member.id}


		AND a.type = #{type}


		AND a.status_system = #{statusSystem}


	    AND a.status_member = #{statusMember}


		AND a.status_pay = #{statusPay}


		AND a.status_judge = #{statusJudge}


		AND (a.order_no LIKE 
			'%'||#{searchParameter.keyWord}||'%'
			'%'+#{searchParameter.keyWord}+'%'
			concat('%',#{searchParameter.keyWord},'%')
		OR m.usercode LIKE 
			'%'||#{searchParameter.keyWord}||'%'
			'%'+#{searchParameter.keyWord}+'%'
			concat('%',#{searchParameter.keyWord},'%')
		)


		AND a.create_date >= #{beginCreateDate}


		AND a.create_date <= #{endCreateDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bus_oe_experience_order a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM bus_oe_experience_order a


	a.del_flag = 0
	AND a.id in (
	SELECT experience_order_id FROM bus_oe_experience_produce b WHERE b.produce_id
	IN

		#{id}

	)

		AND a.status_pay = #{statusPay}


		AND a.type = #{type}


		AND a.appoint_stock_status = #{appointStockStatus}


ORDER BY a.appoint_date ASC

	SELECT count(a.id) FROM bus_oe_experience_order a

		a.del_flag = #{DEL_FLAG_NORMAL} 
		AND a.status_pay not in ('1','2')

            AND a.create_date >= #{beginCreateDate}


            AND a.create_date <= #{endCreateDate}

SELECT COUNT(1)
FROM  bus_oe_experience_order a
WHERE a.member_id = #{memberId}
  AND a.status_system != #{STATUSSYSTEM_CLOSE}
  AND DATEDIFF(appoint_pick_date, a.real_exp_date)>=#{CONFIG_EXPERIENCEDAYS}

SELECT a.id AS "id", a.order_no AS "orderNo", a.member_id AS "member.id", a.experience_pack_id AS "experiencePackId", m.usercode AS "member.usercode", a.type AS "type", a.status_member AS "statusMember", a.status_system AS "statusSystem", a.status_pay AS "statusPay", a.status_judge AS "statusJudge", a.status_logistical AS "statusLogistical", a.close_type AS "closeType", a.close_time AS "closeTime", a.appoint_date_type AS "appointDateType", a.appoint_date AS "appointDate", a.appoint_stock_status AS "appointStockStatus", a.send_time AS "sendTime", a.express_days AS "expressDays", a.real_exp_date AS "realExpDate", a.appoint_pick_date AS "appointPickDate", a.money_produce AS "moneyProduce", a.money_lgt AS "moneyLgt", a.money_back_lgt AS "moneyBackLgt", a.money_experience AS "moneyExperience", a.money_appoint AS "moneyAppoint", a.money_appoint_service AS "moneyAppointService", a.money_total AS "moneyTotal", a.money_paid AS "moneyPaid", a.pay_time AS "payTime", a.pay_type AS "payType", a.pay_channel AS "payChannel", a.receive_name AS "receiveName", a.receive_tel AS "receiveTel", a.receive_area_str AS "receiveAreaStr", a.receive_area_detail AS "receiveAreaDetail", a.member_remarks AS "memberRemarks", a.out_warehouse_id AS "outWarehouse.id", a.full_refund_flag AS "fullRefundFlag", a.money_sett_src_return AS "moneySettSrcReturn", a.money_sett_dec AS "moneySettDec", a.money_sett_decable_beans AS "moneySettDecableBeans", a.money_sett_dec_beans AS "moneySettDecBeans", a.num_sett_dec_beans AS "numSettDecBeans", a.money_sett_overdue AS "moneySettOverdue", a.money_sett_return_express AS "moneySettReturnExpress", a.money_sett_return AS "moneySettReturn", a.settlement_time AS "settlementTime", a.arrearage_amount AS "arrearageAmount", a.experience_pack_id AS "experiencePack.id", a.experience_time AS "experienceTime", a.create_date AS "createDate", ep.id AS "ep.id", ep.produce_id AS "ep.produce.id", p.code AS "ep.produce.code", ep.num AS "ep.num", ep.buy_num AS "ep.buyNum", ep.goods_title AS "ep.goodsTitle", ep.produce_name AS "ep.produceName", ep.price_src_experience AS "ep.priceSrcExperience", ep.scale_experience_deposit AS "ep.scaleExperienceDeposit", ep.price_experience_deposit AS "ep.priceExperienceDeposit", ep.status AS "ep.status", ep.decision_type AS "ep.decisionType", ep.decision_buy_order_id AS "ep.decisionBuyOrder.id", bo.order_no AS "ep.decisionBuyOrder.orderNo", bo.money_lgt AS "ep.decisionBuyOrder.moneyLgt", bo.money_appoint_service AS "ep.decisionBuyOrder.moneyAppointService", ep.money_sett_dec AS "ep.moneySettDec", ep.damage_type AS "ep.damage_type", ep.money_check_dec AS "ep.moneyCheckDec", ep.money_sett_decable_beans AS "ep.moneySettDecableBeans", ep.price_src_buy AS "ep.priceSrcBuy", ep.discount_buy AS "ep.discountBuy", ep.price_buy AS "ep.priceBuy", p.goods_id AS "ep.produce.goods.id", p.code AS "ep.produce.code", p.price_src AS "ep.produce.priceSrc", p.scale_experience_fee AS "ep.produce.scaleExperienceFee", p.scale_experience_deposit AS "ep.produce.scaleExperienceDeposit", p.price_src AS "ep.produce.priceSrc",
g.icon AS "ep.produce.goods.icon", borp.damage_type AS "ep.returnProduct.damageType", borp.dec_money AS "ep.returnProduct.decMoney" FROM bus_oe_experience_order a LEFT JOIN crm_mi_member m on m.id=a.member_id LEFT JOIN bus_oe_experience_produce ep ON ep.experience_order_id = a.id LEFT JOIN lgt_ps_produce p ON p.id = ep.produce_id LEFT JOIN lgt_ps_goods g ON g.id = p.goods_id LEFT JOIN bus_ob_buy_order bo ON bo.id = ep.decision_buy_order_id LEFT JOIN bus_ol_return_order ro ON ro.order_id = a.id LEFT JOIN bus_ol_return_product borp ON borp.product_id = p.id
WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND a.id = #{id}

SELECT MIN(create_date) AS "firstOrderTime",
	   SUM(IFNULL(money_produce, 0)) AS "experienceTotalMoney",
	   COUNT(1) AS "experienceTimes"
  FROM bus_oe_experience_order
 WHERE member_id = #{memberId}
   AND status_system != #{STATUSSYSTEM_CLOSE}

	SELECT

	FROM bus_oe_experience_order a
	WHERE a.member_id = #{memberId}
	  AND a.status_system != #{STATUSSYSTEM_CLOSE}
 ORDER BY a.create_date ASC
    LIMIT 1

mapper.update:
UPDATE bus_oe_experience_order SET
sett_buy = #{settBuy}
WHERE
id = #{id}

UPDATE bus_oe_experience_order SET
status_member = #{STATUSMEMBER_CLOSE},
status_system = #{STATUSSYSTEM_CLOSE},
close_type = #{CLOSETYPE_SYSTEM},
close_time = #{closeTime},
update_date = #{updateDate}
WHERE
del_flag = #{DEL_FLAG_NORMAL}
AND status_system = #{STATUSSYSTEM_TOPAY}
AND status_pay = #{STATUSPAY_TOPAY_FINAL}
AND id=#{id}

UPDATE bus_oe_experience_order SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE bus_oe_experience_order SET

	status_system = #{status},


	real_exp_date = #{realExpDate},

status_logistical = #{statuslogisticalSigned}
WHERE order_no = #{orderNo}
