mapper.select:
SELECT a.*, u.id AS "createBy.id", u.name AS "createBy.name", u.login_name AS "createBy.loginName", c.name AS "createBy.company.name", o.name AS "createBy.office.name" FROM sys_log a JOIN sys_user u ON u.id = a.create_by JOIN sys_office c ON c.id = u.company_id JOIN sys_office o ON o.id = u.office_id WHERE 1 = 1
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND a.create_by = #{createBy.id}
AND u.login_name like '%'||#{createBy.loginName}||'%' '%'+#{createBy.loginName}+'%' CONCAT('%', #{createBy.loginName}, '%')
AND u.name like '%'||#{createBy.name}||'%' '%'+#{createBy.name}+'%' CONCAT('%', #{createBy.name}, '%')
AND a.create_date >= #{beginDate}
AND a.create_date <= #{endDate}
AND a.request_uri LIKE '%'||#{requestUri}||'%' '%'+#{requestUri}+'%' CONCAT('%', #{requestUri}, '%')
AND a.type = #{TYPE_EXCEPTION}
ORDER BY a.create_date DESC
